binding.script:

var self = cmdarg();
var emer_Canopy_Jett_Sx = getprop("sim/G91/switchs/canopy/handle/Emer_Canopy_Jett_Sx");
var canopyOpenSx = getprop("sim/G91/switchs/canopy/EmergencyCanopyOpenSx");
if (emer_Canopy_Jett_Sx == 1 and canopyOpenSx == 0) {
setprop("sim/G91/switchs/canopy/handle/Emer_Canopy_Jett_Sx",0);
}


